/*==================================================
src/components/Debits.js

The Debits component contains information for Debits page view.
Note: You need to work on this file for the Assignment.
==================================================*/
import { Component } from 'react';
import {Link} from 'react-router-dom';

class Debits extends Component{
  constructor(props){
    super(props)
    this.state = {
      description: "",
      amount: "",
    };
  }

  debitsView = () => {
    const { debits } = this.props;
    return debits.map((debit) => {  // Extract "id", "amount", "description" and "date" properties of each debits JSON array element
      let date = debit.date.slice(0,10);
      return <li key={debit.id}>{debit.amount} {debit.description} {date}</li>
    });
  }

  handleSubmit = (event) => {
    event.preventDefault();

    let newDebit = {
      description: this.state.description,
      amount: parseFloat(this.state.amount).toFixed(2), // Round amount to 2 decimal places
    };

    // Call the addDebit function passed as a prop from the parent component
    this.props.addDebit(newDebit);

    // Clear the form input fields after submitting
    this.setState({
      description: "",
      amount: "",
    });
  }

  // Handle input changes for description and amount
  handleDescriptionChange = (event) => {
    this.setState({ description: event.target.value });
  }

  handleAmountChange = (event) => {
    this.setState({ amount: event.target.value });
  }

  render() {
    return (
      <div>
        <h1>Debits</h1>
  
        {this.debitsView()}
        
        <form onSubmit={this.handleSubmit}>
          <input type="text" name="description" value="this.state.description" onChange={(e) => this.setState({description: e.target.value})} />
          <input type="number" name="amount" />
          <button type="submit">Add Debit</button>
        </form>
        <br/>
        <Link to="/">Return to Home</Link>
      </div>
    );
  }
  



  
}

export default Debits;
