/*==================================================
src/components/Debits.js

The Debits component contains information for Debits page view.
Note: You need to work on this file for the Assignment.
==================================================*/
import {Link} from 'react-router-dom';

const Debits = (props) => {
  // Create the list of Debit items
  let debitsView = () => {
    const { debits } = props;
    return debits.map((debit) => {  // Extract "id", "amount", "description" and "date" properties of each debits JSON array element
      let date = debit.date.slice(0,10);
      return <li key={debit.id}>{debit.amount} {debit.description} {date}</li>
    });
  }

  const handleSubmit = (event) => {
    event.preventDefault();

    const round = parseFloat(amount).toFixed(2);

    const newDebit = {
      id: Date.now(),  // Use current timestamp as a unique ID
      amount: parseFloat(round),  // Ensure amount is a number
      description: description,
      date: new Date().toISOString(),  // Get the current date in ISO format
    };

    // Call the addDebit function passed as a prop from the parent component
    props.addDebit(newDebit);
  }

  const round = parseFloat(amount).toFixed(2);

  const newDebit = {
    id
  }
  // Render the list of Debit items and a form to input new Debit item
  return (
    <div>
      <h1>Debits</h1>

      {debitsView()}

      <form onSubmit={props.addDebit}>
        <input type="text" name="description" />
        <input type="number" name="amount" />
        <button type="submit">Add Debit</button>
      </form>
      <br/>
      <Link to="/">Return to Home</Link>
    </div>
  );
}

export default Debits;
