/*==================================================
src/components/Debits.js

The Debits component contains information for Debits page view.
Note: You need to work on this file for the Assignment.
==================================================*/
import { Component } from 'react';
import {Link} from 'react-router-dom';

class Debits extends Component{
  constructor(props){
    super(props)
    this.state = {
      description: "",
      amount: "",
    };
  }

  debitsView = () => {
    const { debits } = this.props;
    return debits.map((debit) => {  // Extract "id", "amount", "description" and "date" properties of each debits JSON array element
      let date = debit.date.slice(0,10);
      return <li key={debit.id}>{debit.amount} {debit.description} {date}</li>
    });
  }

  handleSubmit = (event) => {
    event.preventDefault();

    if (!this.validInputs()) {
      return;
    }

    const newDebit = {
      id: Date.now(), // Unique ID
      description: this.state.description,
      amount: parseFloat(this.state.amount).toFixed(2), // Ensure it's a number with two decimals
      date: new Date().toISOString(), // Current date in ISO format
    };

    // Call the addDebit method passed as a prop
    this.props.addDebit(newDebit);

    // Clear form fields
    this.setState({
      description: "",
      amount: "",
      errors: {},
    });
  };

  // Handle input changes
  handleInputChange = (event) => {
    this.setState({
      [event.target.name]: event.target.value,
    });
  };

  render() {
    return (
      <div>
        <h1>Debits</h1>

        {this.debitsView()}

        <form onSubmit={this.handleSubmit}>
          <div>
            <label>Description:</label>
            <input
              type="text"
              name="description"
              value={this.state.description}
              onChange={this.handleInputChange}
            />
            {this.state.errors.description && (
              <span className="error">{this.state.errors.description}</span>
            )}
          </div>

          <div>
            <label>Amount:</label>
            <input
              type="number"
              name="amount"
              value={this.state.amount}
              onChange={this.handleInputChange}
            />
            {this.state.errors.amount && (
              <span className="error">{this.state.errors.amount}</span>
            )}
          </div>

          <button type="submit">Add Debit</button>
        </form>

        <br />
        <Link to="/">Return to Home</Link>
      </div>
    );
  }
}

export default Debits;
