/*==================================================
src/components/Credits.js

The Credits component contains information for Credits page view.
Note: You need to work on this file for the Assignment.
==================================================*/
import { Link } from 'react-router-dom';
import React, { useState } from 'react';

const Credits = (props) => {
  const {credits, addCredit, accountBalance} = props;
  const [description, setDescription] = useState('');
  const [amount, setAmount] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    if (description && amount) {
      addCredit(description, parseFloat(amount));
      setDescription('');
      setAmount('');
    }
  };

  const formatDate = (dateString) => {
    const options = { year: 'numeric', month: 'long', day: 'numeric' };
    const date = new Date(dateString);
    return date.toLocaleDateString(undefined, options);
  };



  return (
    <div>
      <h1>Credits</h1>

      <form onSubmit={handleSubmit} style={{ marginTop: "20px" }}>
        <input
          type="text"
          placeholder="Description"
          value={description}
          onChange={(e) => setDescription(e.target.value)}
          required
        />
        <input
          type="number"
          placeholder="Amount"
          value={amount}
          onChange={(e) => setAmount(e.target.value)}
          required
        />
        <button type="submit">Add Credit</button>
      </form>

      
      <h3>Account Balance: ${accountBalance.toFixed(2)}</h3>

      <h2 style={{ textAlign: 'center', marginBottom: '20px' }}>Credits List</h2>
      <div>
        {credits.map((credit, index) => (
          <div key={index}>
            <span>{credit.description}</span>
            <span>${credit.amount.toFixed(2)}</span>
            <span>{formatDate(credit.date)}</span>
          </div>
        ))}
      </div>

      <Link to="/">
            <button>Home</button>
      </Link>
    </div>
  );
};

export default Credits;
