import { Link } from 'react-router-dom';
import { useState } from 'react';

const Debits = (props) => {
  const [formData, setFormData] = useState({ description: '', amount: '' });

  // Update state when the input changes
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  // Handle form submission
  const handleSubmit = (e) => {
    e.preventDefault();
    const newDebit = {
      id: Math.random().toString(36).substring(2, 9), // Generate a random id
      description: formData.description,
      amount: parseFloat(formData.amount), // Convert amount to a number
      date: new Date().toISOString(), // Use current date
    };
    props.addDebit(newDebit); // Call the addDebit function passed from parent
    setFormData({ description: '', amount: '' }); // Reset the form
  };

  // Create the list of Debit items
  let debitsView = () => {
    const { debits } = props;
    return debits.map((debit) => {
      let date = debit.date.slice(0, 10);
      return (
        <li key={debit.id}>
          ${debit.amount.toFixed(2)} - {debit.description} on {date}
        </li>
      );
    });
  };

  // Render the list of Debit items and the form
  return (
    <div>
      <h1>Debits</h1>
      <ul>{debitsView()}</ul>
      <form onSubmit={handleSubmit}>
        <label>
          Description:
          <input
            type="text"
            name="description"
            value={formData.description}
            onChange={handleChange}
            required
          />
        </label>
        <label>
          Amount:
          <input
            type="number"
            name="amount"
            value={formData.amount}
            onChange={handleChange}
            step="0.01"
            required
          />
        </label>
        <button type="submit">Add Debit</button>
      </form>
      <br />
      <Link to="/">Return to Home</Link>
    </div>
  );
};

export default Debits;
