/*==================================================
src/components/Debits.js

The Debits component contains information for Debits page view.
Note: You need to work on this file for the Assignment.
==================================================*/
import { Link } from "react-router-dom";
import React, { useState } from "react";

const Debits = (props) => {
  const { debits, addDebit, accountBalance } = props;

  const [description, setDescription] = useState("");
  const [amount, setAmount] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
    if (description && amount > 0) {
      addDebit({ description, amount: parseFloat(amount), date: new Date().toISOString() });
      setDescription("");
      setAmount("");
    }
  };

  const formatDate = (dateString) => {
    const date = new Date(dateString);
    return date.toISOString().slice(0, 10); // Extract YYYY-MM-DD
  };

  // Create the list of Debit items
  const debitsView = () => {
    if (!debits || debits.length === 0) {
      return <p>No debits to display.</p>;
    }

    return (
      <ul>
        {debits.map((debit) => {
          const date = formatDate(debit.date);
          return (
            <li key={debit.id}>
              <strong>Description:</strong> {debit.description}, 
              <strong> Amount:</strong> ${debit.amount.toFixed(2)}, 
              <strong> Date:</strong> {date}
            </li>
          );
        })}
      </ul>
    );
  };

  return (
    <div>
      <h1>Debits</h1>

      {/* Render the debit list */}
      <div>{debitsView()}</div>

      {/* Render the form to add a new debit */}
      <form onSubmit={handleSubmit} style={{ marginTop: "20px" }}>
        <input
          type="text"
          placeholder="Description"
          value={description}
          onChange={(e) => setDescription(e.target.value)}
          required
        />
        <input
          type="number"
          placeholder="Amount"
          value={amount}
          onChange={(e) => setAmount(e.target.value)}
          required
          min="0.01"
          step="0.01"
        />
        <button type="submit">Add Debit</button>
      </form>

      <h3>Account Balance: ${accountBalance.toFixed(2)}</h3>

      <br />
      <Link to="/">Return to Home</Link>
    </div>
  );
};

export default Debits;
