/*==================================================
src/components/Debits.js

The Debits component contains information for Debits page view.
Note: You need to work on this file for the Assignment.
==================================================*/
import {Link} from 'react-router-dom';
import React, { useState } from 'react';

const Debits = (props) => {
  const [description, setDescription] = useState('');
  const [amount, setAmount] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    if (description && amount) {
      props.addDebit(description, amount);
      setDescription('');
      setAmount('');
    }
  };

  
  // Create the list of Debit items
  let debitsView = () => {
    const {debits} = props
    return debits.map((debit) => {  // Extract "id", "amount", "description" and "date" properties of each debits JSON array element
      let date = debit.date.slice(0,10);
      return <li key={debit.id}>{debit.amount} {debit.description} {date}</li>
    });
  }
  // Render the list of Debit items and a form to input new Debit item
  return (
    <div>
      <h1>Debits</h1>

      {debitsView()}

      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="Description"
          value={description}
          onChange={(e) => setDescription(e.target.value)}
          required
        />
        <input
          type="number"
          placeholder="Amount"
          value={amount}
          onChange={(e) => setAmount(e.target.value)}
          required
        />
        <button type="submit">Add Debit</button>
      </form>
        <h2 style={{ textAlign: 'center', marginBottom: '20px' }}>Debits List</h2>
      <div>
        {debits.map((debit, index) => (
          <div key={index} style={cardStyle}>
            <span style={descriptionStyle}>{debit.description}</span>
            <span style={amountStyle}>${debit.amount.toFixed(2)}</span>
            <span style={dateStyle}>{formatDate(debit.date)}</span>
          </div>
        ))}
      </div>
      <br/>
      <Link to="/">Return to Home</Link>
    </div>
  );
}

export default Debits;
